import React,{useState} from 'react';
import { StyleSheet, Text, View,Modal,Alert,KeyboardAvoidingView} from 'react-native';
import {TextInput,Button} from 'react-native-paper'
import * as ImagePicker from 'expo-image-picker';
import * as Permissions from 'expo-permissions';


const SignUp = ({navigation,route})=>{
    const getDetails = (type)=>{
       if(route.params){
          switch(type){
              case "name":
                  return route.params.name
              case "email":
                 return route.params.email
              case "contact":
                return route.params.contact
              case "address":
                  return route.params.address  
              case "password":
                  return  route.params.password
              case "types":
                return  route.params.types  
          }
       }
       return ""
    }
    
    const [name,setName] = useState(getDetails("name"))
    const [email,setEmail] = useState(getDetails("email"))
    const [contact,setContact] = useState(getDetails("contact"))
    const [address,setAddress] = useState(getDetails("address"))
    const [password,setPassword] = useState(getDetails("password"))
    const [types,setTypes] = useState(getDetails("types"))
    const [modal,setModal] = useState(false)
    const [enableshift,setenableShift] = useState(false)

    const submitData = ()=>{
          fetch("http://10.0.2.2:3000/send-data",{
              method:"post",
              headers:{
                'Content-Type': 'application/json'
              },
              body:JSON.stringify({
                
                  name,
                  email,
                  contact,
                  address,
                  password,
                  types
              })
          })
          .then(res=>res.json())
          .then(data=>{
              Alert.alert(`${data.name} is saved successfuly`)
              navigation.navigate("Home")
          })
          .catch(err=>{
            Alert.alert("someting went wrong")
        })
    }

    const updateDetails = ()=>{
        fetch("http://10.0.2.2:3000/update",{
            method:"post",
            headers:{
              'Content-Type': 'application/json'
            },
            body:JSON.stringify({
                id:route.params._id,
                name,
                email,
                contact,
                address,
                password,
                types
            })
        })
        .then(res=>res.json())
        .then(data=>{
            Alert.alert(`${data.name} is updated successfuly`)
            navigation.navigate("Login")
        })
        .catch(err=>{
          Alert.alert("someting went wrong")
      })
    }

   const pickFromGallery = async ()=>{
      const {granted} =  await Permissions.askAsync(Permissions.CAMERA_ROLL)
      if(granted){
           let data =  await ImagePicker.launchImageLibraryAsync({
                mediaTypes:ImagePicker.MediaTypeOptions.Images,
                allowsEditing:true,
                aspect:[1,1],
                quality:0.5
            })
            if(!data.cancelled){
                let newfile = { 
                  uri:data.uri,
                  type:`test/${data.uri.split(".")[1]}`,
                  name:`test.${data.uri.split(".")[1]}` 
                  
              }
                handleUpload(newfile)
            }
      }else{
         Alert.alert("you need to give up permission to work")
      }
   }
   const pickFromCamera = async ()=>{
      const {granted} =  await Permissions.askAsync(Permissions.CAMERA)
      if(granted){
           let data =  await ImagePicker.launchCameraAsync({
                mediaTypes:ImagePicker.MediaTypeOptions.Images,
                allowsEditing:true,
                aspect:[1,1],
                quality:0.5
            })
          if(!data.cancelled){
              let newfile = { 
                uri:data.uri,
                type:`test/${data.uri.split(".")[1]}`,
                name:`test.${data.uri.split(".")[1]}` 

            }
              handleUpload(newfile)
          }
      }else{
         Alert.alert("you need to give up permission to work")
      }
   }


   const handleUpload = (image)=>{
        const data = new FormData()
        data.append('file',image)
        data.append('upload_preset','employeeApp')
        data.append("cloud_name","mukeshph66")

        fetch("https://api.cloudinary.com/v1_1/mukeshph66/image/upload",{
            method:"post",
            body:data
        }).then(res=>res.json()).
        then(data=>{
            setPicture(data.url)
            setModal(false)
        }).catch(err=>{
            Alert.alert("error while uploading")
        })
   }
   

    return(
     <KeyboardAvoidingView behavior="position" style={styles.root} enabled={enableshift}>
        <View >
            <TextInput
                label='Name'
                style={styles.inputStyle}
                value={name}
                onFocus={()=>setenableShift(false)}
                theme={theme}
                mode="outlined"
                onChangeText={text => setName(text)}
            />
            <TextInput
                label='Email'
                style={styles.inputStyle}
                value={email}
                theme={theme}
                onFocus={()=>setenableShift(false)}
                mode="outlined"
                onChangeText={text => setEmail(text)}
            />
            <TextInput
                label='phone'
                style={styles.inputStyle}
                value={contact}
                theme={theme}
                onFocus={()=>setenableShift(false)}
                keyboardType="number-pad"
                mode="outlined"
                onChangeText={text =>setContact(text)}
            />
             
            <TextInput
                label='salary'
                style={styles.inputStyle}
                value={address}
                theme={theme}
                onFocus={()=>setenableShift(true)}
                mode="outlined"
                onChangeText={text =>setAddress(text)}
            />
            <TextInput
                label='position'
                style={styles.inputStyle}
                value={password}
                theme={theme}
                onFocus={()=>setenableShift(true)}
                mode="outlined"
                onChangeText={text =>setPassword(text)}
            />
            <TextInput
                label='Types'
                style={styles.inputStyle}
                value={types}
                onFocus={()=>setenableShift(false)}
                theme={theme}
                mode="outlined"
                onChangeText={text => setTypes(text)}
            />
             {route.params?
             <Button 
             style={styles.inputStyle}
             icon="content-save"
              mode="contained" 
              theme={theme}
              onPress={() => updateDetails()}>
                   Update details
             </Button>
             : 
             <Button 
             style={styles.inputStyle}
             icon="content-save"
              mode="contained" 
              theme={theme}
              onPress={() => submitData()}>
                   save
             </Button>
             }         
      </View>
      </KeyboardAvoidingView>
     
 
    )
}

const theme = {
    colors:{
        primary:"#006aff"
    }
}
const styles=StyleSheet.create({
    root:{
       flex:1,
    },
    inputStyle:{
        margin:5
    },
    modalView:{
        position:"absolute",
        bottom:2,
        width:"100%",
        backgroundColor:"white"

    },
    modalButtonView:{
        flexDirection:"row",
        justifyContent:"space-around",
        padding:10
    }
})

export default SignUp


