import * as React from 'react';
import { images, SIZES, COLORS, FONTS } from '../constants';
import { Divider, SearchBar, Header, Card, ListItem, Avatar } from 'react-native-elements';
import {
    StyleSheet,
    View,
    TouchableOpacity,
    ScrollView
} from 'react-native';


export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
        };
    }

    componentDidMount() {
        fetch('http://192.168.10.9:3210/Users')
            .then(res => res.json())
            .then(result => {
                this.setState({
                    items: result
                });
            });
    }

    render() {
        const { items } = this.state;
        return (
            <ScrollView backgroundColor = '003f5c' contentContainerStyle={styles.contentContainer}>
            <View >
                {
                    items.map((l, i) => (
                        <TouchableOpacity
                            style={{ justifyContent: 'center', marginHorizontal: SIZES.base }}
                            onPress={() => { console.log("Law : " + l.name + " " + l.subtitle) }}
                        >
                            <ListItem key={i++} bottomDivider >
                                <ListItem.Content >
                                    <ListItem.Title>{l.ID}</ListItem.Title>
                                    <ListItem.Subtitle>{l.Name}</ListItem.Subtitle>
                                    <ListItem.Subtitle>{l.Email}</ListItem.Subtitle>
                                    <ListItem.Subtitle>{l.Address}</ListItem.Subtitle>
                                    <ListItem.Subtitle>{l.Number}</ListItem.Subtitle>
                                    <ListItem.Subtitle>{l.Password}</ListItem.Subtitle>
                                </ListItem.Content>
                            </ListItem>
                        </TouchableOpacity>
                    ))
                }
            </View>
            </ScrollView>
        )
    };
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#003f5c",
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,

        elevation: 5,
    }
});
