import * as React from 'react';
import { images, SIZES, COLORS, FONTS } from '../constants';
import { Divider, SearchBar, Header, Card, ListItem, Avatar } from 'react-native-elements';
import {
    StyleSheet,
    View,
    TouchableOpacity
} from 'react-native';
const list = [
    //Dummy Data
    {

        name: 'Case1',
        Date: '14-june-2021',
        Description: 'Your case for ihtesham mansoor'
    },
    {
        name: 'Case2',
        Date: '15-june-2021',
        Description: 'Hearing soon'
    },
    {
        name: 'payment',
        Date: '18-june-2021',
        Description: 'Haris kiyani payment should be done!'
    },

    {
        name: 'Payment',
        Date: '15-may-2021',
        Description: 'Haris kiyani payment should be done!'
    },

]
export default class App extends React.Component {
    state = {
        search: '',
    };

    updateSearch = (search) => {
        this.setState({ search });
    };

    render() {
        const { search } = this.state;
        return (
            <View style={styles.container}>
                <Divider style={{ width: "50%", margin: -5 }} />
                <Header
                    containerStyle={{ width: 400 }}
                    leftComponent={{ text: "", style: { color: 'black', fontSize: 20 } }}
                    placement="left"
                />
                <Card containerStyle={{ backgroundColor: "#003f5c" }}  >
                    <Card.Title style={{ color: COLORS.white, ...FONTS.h3 }}>Reminder</Card.Title>
                    <Card.Divider />
                    <View >
                        {

                            list.map((l, i) => (
                                <TouchableOpacity
                                    style={{ justifyContent: 'center', marginHorizontal: SIZES.base }}
                                    onPress={() => { console.log("Law : " + l.name + " " + l.subtitle) }}
                                >
                                    <ListItem key={i} bottomDivider >
                                        <ListItem.Content >
                                            <ListItem.Title>{l.name}</ListItem.Title>
                                            <ListItem.Title>{l.Date}</ListItem.Title>
                                            <ListItem.Subtitle>{l.Description}</ListItem.Subtitle>
                                        </ListItem.Content>
                                    </ListItem>
                                </TouchableOpacity>
                            ))
                        }
                    </View>
                </Card>

            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#003f5c",
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,

        elevation: 5,
    }
});
