import React, { Component } from 'react';
import {
    StyleSheet,
    View,
    Image,
    Text,
    TouchableOpacity,

} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

import { images, icons, COLORS, FONTS, SIZES } from '../constants';
class Client extends Component {
    constructor(props) {
        super(props)
        this.state = {
            items: [],
            UserName: '',
            UserEmail: '',
            UserContact: '',
            UserAddress: '',
            UserPassword: '',
            UserType: ''
        }
    }
    UserSignupFunction = () => {
        console.log("UserName");
        const { items } = this.state;
        const { UserName } = this.state;
        const { UserEmail } = this.state;
        const { UserContact } = this.state;
        const { UserAddress } = this.state;
        const { UserPassword } = this.state;
        const { UserType } = this.state;
        console.log("1234");

        fetch('http://192.168.10.9:3210/Cases', {
        })
            .then(res => res.json())
            .then(result => {
                this.setState({
                    items: result
                });
                console.log(items);
            });
    // If server response message same as Data Matched
    var i = 0;
    while (items[i]) {
      if (items[i].Email == mial) {
        if (items[i].Password == pass) {
          if (items[i].User_Type == "Lawyer") {
            UserName  = items[i].Name;
            UserEmail  = items[i].Email;
             UserContact  = items[i].Contact;
             UserAddress  = items[i].Address;
             UserPassword  = items[i].Password;
             UserType  = items[i].User_Type;    
            console.log("Successfully Login");
            break;
          } else if (items[i].User_Type == "Client") {
            UserName  = items[i].Name;
            UserEmail  = items[i].Email;
             UserContact  = items[i].Contact;
             UserAddress  = items[i].Address;
             UserPassword  = items[i].Password;
             UserType  = items[i].User_Type;    
            console.log("Successfully Login");
            break;
          }
        } else {
          i++;
        }
      }
      else {
        i++;
      }
    }
console.log("UserName"+ UserName);
  }

render() {
    const { UserEmail } = this.state;
    return (
        <View style={styles.container}>
            {/* Banner */}
            <View style={{ flex: 1, marginTop: SIZES.base, paddingHorizontal: SIZES.padding, }}>
                <Image
                    source={images.homeBanner}
                    resizeMode="cover"
                    style={{
                        width: "100%",
                        height: "100%",
                        borderRadius: 15,
                    }}
                />
            </View>

            {/* Options */}
            <View style={{ flex: 1, justifyContent: 'center' }}>
                <View style={{ flexDirection: 'row', marginTop: SIZES.padding, paddingHorizontal: SIZES.base }}>
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#46aeff', '#5884ff']}
                        label="Cases"
                        onPress={() => this.props.navigation.navigate('Cl_Cases')}
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#fddf90', '#fcda13']}
                        label="Lawyers"
                        onPress={() => this.props.navigation.navigate("Cl_Lawyer")}
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#e973ad', '#da5df2']}
                        label="Chatting"
                        onPress={() => this.props.navigation.navigate("Cl_Chat")}
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#fcaba8', '#fe6bba']}
                        label="Dictionary"
                        onPress={() => this.props.navigation.navigate("Cl_Dictionary")}
                    />
                </View>

                <View style={{ flexDirection: 'row', marginTop: SIZES.radius, paddingHorizontal: SIZES.base }}>
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#ffc465', '#ff9c5f']}
                        label="Hearings"
                        onPress={() => this.props.navigation.navigate("Cl_Hearings")}
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#7cf1fb', '#4ebefd']}
                        label="Profile"
                        onPress={() => {this.UserSignupFunction} }
                        onPress={() => this.props.navigation.navigate("Cl_Profile")} 
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#7be993', '#46caaf']}
                        label="Reminder"
                        onPress={() => this.props.navigation.navigate("Cl_Reminder")} 
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#fca397', '#fc7b6c']}
                        label="Reviews"
                        onPress={() => this.props.navigation.navigate("Cl_Review")}
                    />
                </View>
            </View>

            {/* Destination */}


        </View>
    );
    };
}

const OptionItem = ({ bgColor, icon, label, onPress }) => {
    return (
        <TouchableOpacity
            style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}
            onPress={onPress}
        >
            <View style={[styles.shadow, { width: 60, height: 60 }]}>
                <LinearGradient
                    style={[{ flex: 1, alignItems: 'center', justifyContent: 'center', borderRadius: 15, backgroundColor: 'red' }]}
                    colors={bgColor}
                    start={{ x: 0, y: 0 }}
                    end={{ x: 0, y: 1 }}
                >
                    <Image
                        source={icon}
                        resizeMode="cover"
                        style={{
                            tintColor:  "#003f5c",
                            width: 30,
                            height: 30,
                        }}
                    />
                </LinearGradient>
            </View>
            <Text style={{ marginTop: SIZES.base, color: COLORS.gray, ...FONTS.body3 }}>{label}</Text>
        </TouchableOpacity>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#003f5c",
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,

        elevation: 5,
    }
});

export default Client;
