import React from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity } from 'react-native';
//import axios from 'axios';

//var mysql = require('mysql');
//import mysql from 'D:/LCRN03-trip-booking-app-expo-master/Express_Backend/node_modules/mysql';
var Username;
function insertData(user) {
  console.log("User : " + user);
  Username = user;
  // state = {
  //   Username: "",
  //   email: "",
  //   Contact: "",
  //   Address: "",
  //   password: ""
  // }
}
function PrintData() {
  console.log("Final UserName : " + Username);
}

const SignUp = ({ navigation }) => {

  return (
    <View style={styles.container}>
      <Text style={styles.logo}>Sign Up!</Text>
      <View style={styles.inputView} >
        <TextInput
          style={styles.inputText}
          placeholder="Username. . ."
          placeholderTextColor="#003f5c"
          onChangeText={text => insertData(text)} />
      </View>
      <View style={styles.inputView} >
        <TextInput
          style={styles.inputText}
          placeholder="Email. . ."
          placeholderTextColor="#003f5c"
          onChangeText={text => this.setState({ email: text })} />
      </View>
      <View style={styles.inputView} >
        <TextInput
          style={styles.inputText}
          placeholder="Contact No."
          placeholderTextColor="#003f5c"
          onChangeText={text => this.setState({ Contact: text })} />
      </View>
      <View style={styles.inputView} >
        <TextInput
          style={styles.inputText}
          placeholder="Address. . ."
          placeholderTextColor="#003f5c"
          onChangeText={text => this.setState({ Address: text })} />
      </View>
      <View style={styles.inputView} >
        <TextInput
          secureTextEntry
          style={styles.inputText}
          placeholder="Password..."
          placeholderTextColor="#003f5c"
          onChangeText={text => this.setState({ password: text })} />
      </View>

      <View style={styles.inputView} >
        <TextInput
          secureTextEntry
          style={styles.inputText}
          placeholder="confirm Password..."
          placeholderTextColor="#003f5c"
          onChangeText={text => this.setState({ password: text })} />
      </View>
      <TouchableOpacity onPress={() => console.log("222")} style={styles.loginBtn} >
        <Text style={styles.loginText}>Signup</Text>

      </TouchableOpacity >
      <TouchableOpacity onPress={() => insert()} >
        <Text> </Text>
        <Text style={styles.loginText}>login!</Text>
      </TouchableOpacity>


    </View>
  );
};
function insert() {
  console.log("Added11");
    fetch('http://localhost:3210/data')
      .then(response => response.json())
      .then(data => console.warn("Data : "+data))
      console.warn("Added153");
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#003f5c',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    fontWeight: "bold",
    fontSize: 50,
    color: "#fb5b5a",
    marginBottom: 40
  },
  inputView: {
    width: "80%",
    backgroundColor: "#465881",
    borderRadius: 25,
    height: 50,
    marginBottom: 20,
    justifyContent: "center",
    padding: 20
  },
  inputText: {
    height: 50,
    color: "white"
  },
  forgot: {
    color: "white",
    fontSize: 11
  },
  loginBtn: {
    width: "80%",
    backgroundColor: "#fb5b5a",
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    marginBottom: 10
  },
  loginText: {
    color: "white"
  }
});

export default SignUp;
