import React,{useState} from 'react';
import { StyleSheet, Text, View,Alert} from 'react-native';
import {TextInput,Button} from 'react-native-paper'
 import { TouchableOpacity } from 'react-native';


const SignUp = ({navigation,route})=>{
    const getDetails = (type)=>{
       if(route.params){
          switch(type){
              case "name":
                  return route.params.name
              case "email":
                 return route.params.email
              case "contact":
                return route.params.contact
              case "address":
                  return route.params.address  
              case "password":
                  return  route.params.password
              case "types":
                return  route.params.types  
          }
       }
       return ""
    }
    
    const [name,setName] = useState(getDetails("name"))
    const [email,setEmail] = useState(getDetails("email"))
    const [contact,setContact] = useState(getDetails("contact"))
    const [address,setAddress] = useState(getDetails("address"))
    const [password,setPassword] = useState(getDetails("password"))
    const [types,setTypes] = useState(getDetails("types"))

    const submitData = ()=>{
        console.log("In submit data");
        //   fetch("http://localhost:3000/send-data",{
        //       method:"post",
        //       headers:{
        //         'Content-Type': 'application/json'
        //       },
        //       body:JSON.stringify({
                
        //           name,
        //           email,
        //           contact,
        //           address,
        //           password,
        //           types
        //       })
        //   })
        //   .then(res=>res.json())
        //   .then(data=>{
        //       Alert.alert(`${data.name} is saved successfuly`)
        //       navigation.navigate("Login")
        //   })
        //   .catch(err=>{
        //     Alert.alert("someting went wrong")
        // })
    }

    const updateDetails = ()=>{
        console.log("In Update Details");
        fetch("http://localhost:3000/update",{
            method:"post",
            headers:{
              'Content-Type': 'application/json'
            },
            body:JSON.stringify({
                id:route.params._id,
                name,
                email,
                contact,
                address,
                password,
                types
            })
        })
        .then(res=>res.json())
        .then(data=>{
            Alert.alert(`${data.name} is updated successfuly`)
            navigation.navigate("Login")
        })
        .catch(err=>{
          Alert.alert("someting went wrong")
      })
    }
    return(
        <View >
            <TextInput
                label='Name'
                style={styles.inputStyle}
                value={name}
                theme={theme}
                mode="outlined"
                onChangeText={text => setName(text)}
            />
            <TextInput
                label='Email'
                style={styles.inputStyle}
                value={email}
                theme={theme}
                mode="outlined"
                onChangeText={text => setEmail(text)}
            />
            <TextInput
                label='phone'
                style={styles.inputStyle}
                value={contact}
                theme={theme}
                keyboardType="number-pad"
                mode="outlined"
                onChangeText={text =>setContact(text)}
            />
             
            <TextInput
                label='salary'
                style={styles.inputStyle}
                value={address}
                theme={theme}
                mode="outlined"
                onChangeText={text =>setAddress(text)}
            />
            <TextInput
                label='position'
                style={styles.inputStyle}
                value={password}
                theme={theme}
                mode="outlined"
                onChangeText={text =>setPassword(text)}
            />
            <TextInput
                label='Types'
                style={styles.inputStyle}
                value={types}
                theme={theme}
                mode="outlined"
                onChangeText={text => setTypes(text)}
            />
             <Button 
             style={styles.inputStyle}
             icon="content-save"
              mode="contained" 
              theme={theme}
              onPress={() => console.log("In submit data1")}>
                   Update
             </Button>

             <Button 
             style={styles.inputStyle}
             icon="content-save"
              mode="contained" 
              theme={theme}
              onPress={() => console.log("In submit data2")}>
                   Sign Up!
             </Button>


        <TouchableOpacity onPress={() =>  submitData()}
          style={styles.loginBtn}>
          <Text style={styles.loginText}>Signup</Text>
        </TouchableOpacity>

        <TouchableOpacity onPress={() => console.log("In submit data")} >
          <Text> </Text>
          <Text style={styles.loginText}>login!</Text>
        </TouchableOpacity>

        </View >

 
    )
}

const theme = {
    colors:{
        primary:"#006aff"
    }
}
const styles=StyleSheet.create({
    root:{
       flex:1,
    },
    inputStyle:{
        margin:5
    },
    modalView:{
        position:"absolute",
        bottom:2,
        width:"100%",
        backgroundColor:"white"

    },
    modalButtonView:{
        flexDirection:"row",
        justifyContent:"space-around",
        padding:10
    }
})

export default SignUp


