import React, { useState, useEffect } from 'react';
import {
    SafeAreaView,
    Text,
    StyleSheet,
    View,
    FlatList,
    TextInput,
    TouchableOpacity,
    Alert
} from 'react-native';
const App = ({ navigation }) => {
    const [search, setSearch] = useState('');
    const [filteredDataSource, setFilteredDataSource] = useState([]);
    const [masterDataSource, setMasterDataSource] = useState([]);
    var client;
    var lawyer;
    var message;
    var status;
    useEffect(() => {
        fetch(global.link + '/getcasesRequest/' + global.MyName)
            .then((res) => res.json())
            .then((responseJson) => {
                setFilteredDataSource(responseJson);
                setMasterDataSource(responseJson);
                console.log(responseJson);
            })
            .then(result => {
                ({
                    item: result
                });
            })
            .catch((error) => {
                console.log("Error!");
                console.error(error);
            });
    }, []);
    const searchFilterFunction = (text) => {
        // Check if searched text is not blank
        if (text) {
            // Inserted text is not blank
            // Filter the masterDataSource
            // Update FilteredDataSource
            const newData = masterDataSource.filter(
                function (item) {
                    const itemData = item.name
                        ? item.client.toUpperCase()
                        : ''.toUpperCase();
                    const textData = text.toUpperCase();
                    return itemData.indexOf(textData) > -1;
                });
            setFilteredDataSource(newData);
            setSearch(text);
        } else {
            // Inserted text is blank
            // Update FilteredDataSource with masterDataSource
            setFilteredDataSource(masterDataSource);
            setSearch(text);
        }
    };

    const ItemView = ({ item }) => {
        //If server response message same as Data Matched
        return (
            // Flat List Item
            <Text
                style={styles.itemStyle}
                onPress={() => getItem(item)}>
                {item.client}
                {' : '}
                {item.name}
            </Text>
        );
    };

    const ItemSeparatorView = () => {
        return (
            // Flat List Item Separator
            <View
                style={{
                    height: 1,
                    width: '100%',
                    backgroundColor: 'black',
                }}
            />
        );
    };

    const getItem = (item) => {
        // Function for click on an item
        Alert.alert(
            "Lawyer Details",
            "Client Name : " + item.client + "\nCase Description : " + item.description,
            [
                {
                    text: "Back",
                    style: "cancel"
                },
                { text: "Accept Case", onPress: () => AcceptingRequest(item) }
            ]
        );
    };
    const AcceptingRequest = (item) => {
        Alert.alert(
            "Are you sure!",
            "You want to accept case",
            [
                {
                    text: "Reject",
                    onPress: () => Reject(item),
                },
                {
                    text: "Accept",
                    onPress: password => Accept(item),

                }
            ],

        );
    }
    const Reject = (item) => {
        client = item.client;
        lawyer = global.MyName;
        message =""; 
        status = "Case Rejected";
        fetch(global.link + "/Chating_Member", {
            method: "post",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                client,
                lawyer,
                message,
                status,
            })
        })
        .then(res => res.json())
        .then(data => {
            Alert.alert(`Request Rejeccted`)
            fetch(global.link + '/deleteRequest/' + item._id, {
                method: 'DELETE'
            })
                .then(() =>
                    navigation.navigate("Lawyer"),
                )
                .catch(error => {
                    setErrorMessage(error);
                    console.error('There was an error!', error);
                });

        })
        .catch(err => {
            Alert.alert("someting went wrong")
        })
    }
    const back = () => {
        Alert.alert(`Request Remove successfuly`);
    }
    const Accept = (item, password) => {
        client = item.client;
        lawyer = global.MyName;
        message =""; 
        status = "Case Accepted";
        fetch(global.link + "/Chating_Member", {
            method: "post",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                client,
                lawyer,
                message,
                status,
            })
        })
            .then(res => res.json())
            .then(data => {
                Alert.alert(`Request Accepted`)
                fetch(global.link + '/deleteRequest/' + item._id, {
                    method: 'DELETE'
                })
                    .then(() =>
                        navigation.navigate("Lawyer"),
                    )
                    .catch(error => {
                        setErrorMessage(error);
                        console.error('There was an error!', error);
                    });

            })
            .catch(err => {
                Alert.alert("someting went wrong")
            })
    }
    return (
        <SafeAreaView style={{ flex: 1 }}>
            <View style={styles.container}>
                <TextInput
                    style={styles.textInputStyle}
                    onChangeText={(text) => searchFilterFunction(text)}
                    value={search}
                    underlineColorAndroid="transparent"
                    placeholder="Search Requests!"
                />
                <FlatList
                    data={filteredDataSource}
                    keyExtractor={(item, index) => index.toString()}
                    ItemSeparatorComponent={ItemSeparatorView}
                    renderItem={ItemView}
                />
            </View>
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container: {
        backgroundColor: 'white',
    },
    itemStyle: {
        padding: 10,
    },
    textInputStyle: {
        height: 40,
        borderWidth: 1,
        paddingLeft: 20,
        margin: 5,
        borderColor: '#009688',
        backgroundColor: 'white',
    },
});
export default App;
