import * as React from 'react';
import { images, icons, SIZES, COLORS, FONTS } from '../constants';
import { Tab, Divider, SearchBar, Header, Icon, Card, ListItem, Avatar } from 'react-native-elements';
import {
    StyleSheet,
    View,
    Image,
    Text,
    TouchableOpacity,
    ScrollView
} from 'react-native';

var NameUser;
var UserEmail;
var UserContact;
var UserAddress;
var UserPassword;
export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
        };
    }
    state = {
        search: '',
    };

    updateSearch = (search) => {
        this.setState({ search });
    };

    render() {
        const { items } = this.state;
        const { search } = this.state;
        const {  UserName} = this.state;
        fetch('http://192.168.10.9:3210/Users')
            .then(res => res.json())
            .then(result => {
                this.setState({
                    items: result
                });
        // If server response message same as Data Matched
        var i = 0;
        while (items[i++]) {
          if (items[i].User_Type == "Client" ) {
                NameUser = result[i].Name;
                UserEmail = result[i].Email;
                UserContact = result[i].Number;
                UserAddress = result[i].Address;
                UserPassword = result[i].Password;
                break;
              }
          else {
            i++;
          }
        }
    });

        return (
            
            <View style={styles.container}>
                <Divider style={{ width: "50%", margin: -5 }} />
                <Header
                    containerStyle={{ width: 400 }}
                    leftComponent={{ text: "", style: { color: 'black', fontSize: 20 } }}
                    placement="left"
                />
                <Card containerStyle={{ backgroundColor: "#003f5c" }} wrapperStyle={{}} >
                    <Card.Title style={{ color: COLORS.white, ...FONTS.h3 }}>Search Lawyer</Card.Title>
                    <Card.Divider />
                    <View>
                        <SearchBar
                            placeholder="Type Here..."
                            onChangeText={this.updateSearch}
                            value={search}
                        />
                    </View>
                </Card>
                <ScrollView backgroundColor='003f5c' contentContainerStyle={styles.contentContainer}>

                    <Card containerStyle={{ backgroundColor: "#003f5c" }}  >
                        <Card.Title style={{ color: COLORS.white, ...FONTS.h3 }}>Lawyer</Card.Title>
                        <Card.Divider />
                        <View >
                            {
                                items.map((l, i) => (
                                    <TouchableOpacity
                                        style={{ justifyContent: 'center', marginHorizontal: SIZES.base }}
                                    >
                                        <ListItem key={i++} bottomDivider >
                                            <ListItem.Content >
                                                <ListItem.Title>Lawyer Name :{l.NameUser}</ListItem.Title>
                                                <ListItem.Subtitle>Email : {l.UserEmail}</ListItem.Subtitle>
                                                <ListItem.Subtitle>mobile : {l.UserContact}</ListItem.Subtitle>
                                            </ListItem.Content>
                                        </ListItem>
                                    </TouchableOpacity>
                                ))
                            }
                        </View>
                    </Card>
                </ScrollView>
            </View>
        );
    }
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#003f5c",
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,

        elevation: 5,
    }
});
