import React,{useState} from 'react';
import { StyleSheet, Text, View,Alert} from 'react-native';
import {TextInput,Button} from 'react-native-paper'
import { TouchableOpacity } from 'react-native';
import { Component } from 'react';

const SignUp = ({navigation,route})=>{
    const getDetails = (type)=>{
       if(route.params){
          switch(type){
              case "name":
                  return route.params.name
              case "email":
                 return route.params.email
              case "contact":
                return route.params.contact
              case "address":
                  return route.params.address  
              case "password":
                  return  route.params.password
              case "types":
                return  route.params.types  
          }
       }
       return ""
    }
    
    const [name,setName] = useState(getDetails("name"))
    const [email,setEmail] = useState(getDetails("email"))
    const [contact,setContact] = useState(getDetails("contact"))
    const [address,setAddress] = useState(getDetails("address"))
    const [password,setPassword] = useState(getDetails("password"))
    const [types,setTypes] = useState(getDetails("types"))
    const submitData = ()=>{
        console.log("In submit data");
        if(name == ""){
            Alert.alert(`Please Enter Name`)
        }else if(email == ""){
            Alert.alert(`Please Enter Email`)
        }else if(password == ""){
            Alert.alert(`Please Enter Password`)
        }else{
            fetch(global.link+"/send-data",{
                method:"post",
                    headers:{
                    'Content-Type': 'application/json'
                    },
                    body:JSON.stringify({
                        name,
                        email,
                        contact,
                        address,
                        password,
                        types
                    })
                })
                .then(res=>res.json())
                .then(data=>{
                    Alert.alert(`${data.name} is saved successfuly`)
                    navigation.navigate("Login")
                })
                .catch(err=>{
                Alert.alert("someting went wrong")
            })
        }
    }

    const updateDetails = ()=>{
        console.log("In Update Details");
        fetch("http://localhost:3000/update",{
            method:"post",
            headers:{
              'Content-Type': 'application/json'
            },
            body:JSON.stringify({
                id:route.params._id,
                name,
                email,
                contact,
                address,
                password,
                types
            })
        })
        .then(res=>res.json())
        .then(data=>{
            Alert.alert(`${data.name} is updated successfuly`)
            navigation.navigate("Login")
        })
        .catch(err=>{
          Alert.alert("someting went wrong")
      })
    }

    return (
      <View style={styles.container}>
        <Text style={styles.logo}>Sign Up!</Text>
        <View style={styles.inputView} >
          <TextInput
            style={styles.inputText}
            placeholder="Username. . ."
            placeholderTextColor="#003f5c"
            onChangeText={text => setName(text)}
    />
        </View>
        <View style={styles.inputView} >
          <TextInput
            style={styles.inputText}
            placeholder="Email. . ."
            placeholderTextColor="#003f5c"
            onChangeText={text => setEmail(text)}
    />
        </View>
        <View style={styles.inputView} >
          <TextInput
            style={styles.inputText}
            placeholder="Contact No."
            placeholderTextColor="#003f5c"
            onChangeText={text => setContact(text)}
         />
        </View>
        <View style={styles.inputView} >
          <TextInput
            style={styles.inputText}
            placeholder="Address. . ."
            placeholderTextColor="#003f5c"
            onChangeText={text => setAddress(text)}
            />
        </View>
        <View style={styles.inputView} >
          <TextInput
            secureTextEntry
            style={styles.inputText}
            placeholder="Password..."
            placeholderTextColor="#003f5c"
            onChangeText={text => setPassword(text)}
             />
        </View>

        <View style={styles.inputView} >
          <TextInput
            secureTextEntry
            style={styles.inputText}
            placeholder="confirm Password..."
            placeholderTextColor="#003f5c"
            onChangeText={text => setTypes(text)}
             />
        </View>

        <TouchableOpacity 
              onPress={() => submitData()}
              style={styles.loginBtn}
              >

          <Text style={styles.loginText}>Signup</Text>
        </TouchableOpacity>
        <TouchableOpacity 
        onPress={() => navigation.navigate("Login")} 
        style={styles.loginBtn}>
          <Text style={styles.loginText}>login!</Text>
        </TouchableOpacity>


      </View>
    );

  }


  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#003f5c',
      alignItems: 'center',
      justifyContent: 'center',
    },
    logo: {
      fontWeight: "bold",
      fontSize: 50,
      color: "#fb5b5a",
      marginBottom: 40
    },
    inputView: {
      width: "80%",
      backgroundColor: "#465881",
      borderRadius: 25,
      height: 50,
      marginBottom: 20,
      justifyContent: "center",
      padding: 20
    },
    inputText: {
      height: 50,
      color: "white"
    },
    forgot: {
      color: "white",
      fontSize: 11
    },
    loginBtn: {
      width: "80%",
      backgroundColor: "#fb5b5a",
      borderRadius: 25,
      height: 50,
      alignItems: "center",
      justifyContent: "center",
      marginTop: 40,
      marginBottom: 10
    },
    loginText: {
      color: "white"
    }
  });

export default SignUp



