import * as React from 'react';
import { images, icons, SIZES ,COLORS, FONTS } from '../constants';
import { Tab, Divider, SearchBar, Header, Icon, Card, ListItem, Avatar } from 'react-native-elements';
import {
    StyleSheet,
    View,
    Image,
    Text,
    TouchableOpacity
} from 'react-native';
const list = [
    //Dummy Data
    {
        name: 'Ihtesham Mansoor',
        avatar_url: "",
        subtitle: 'Family Lawyer'
    },
    {
        name: 'Ihtesham Mansoor',
        avatar_url: "",
        subtitle: 'Family Lawyer'
    },
    {
        name: 'Ihtesham Mansoor',
        avatar_url: "",
        subtitle: 'Family Lawyer'
    },

    {
        name: 'HARIS KAYANI',
        avatar_url: "",
        subtitle: 'Legel Lawyer'
    },

]
export default class App extends React.Component {
    state = {
        search: '',
    };

    updateSearch = (search) => {
        this.setState({ search });
    };

    render() {
        const { search } = this.state;
        return (
            <View style={styles.container}>
                <Divider style={{ width: "50%", margin: -5 }} />
                <Header
                    containerStyle={{ width: 400 }}
                    leftComponent={{ text: "", style: { color: 'black', fontSize: 20 } }}
                    placement="left"
                />
                <Card containerStyle={{ backgroundColor: "#003f5c" }} wrapperStyle={{}} >
                    <Card.Title style={{ color: COLORS.white, ...FONTS.h3 }}>Search Case</Card.Title>
                    <Card.Divider />
                    <View>
                        <SearchBar
                            placeholder="Type Here..."
                            onChangeText={this.updateSearch}
                            value={search}
                        />
                    </View>
                </Card>
                <Card containerStyle={{ backgroundColor: "#003f5c" }}  >
                    <Card.Title style={{ color: COLORS.white, ...FONTS.h3 }}>Cases</Card.Title>
                    <Card.Divider />
                    <View >
                        {

                            list.map((l, i) => (
                                <TouchableOpacity
                                    style={{ justifyContent: 'center', marginHorizontal: SIZES.base }}
                                    onPress={() => { console.log("Law : " + l.name + " " + l.subtitle) }}
                                >
                                    <ListItem key={i} bottomDivider >
                                    <Avatar source={{ uri: l.avatar_url }} />
                                        <ListItem.Content >
                                            <ListItem.Title>{l.name}</ListItem.Title>
                                            <ListItem.Subtitle>{l.subtitle}</ListItem.Subtitle>
                                        </ListItem.Content>
                                    </ListItem>
                                </TouchableOpacity>
                            ))
                        }
                    </View>
                </Card>

            </View>
        );
    }
}
//<Avatar source={{ uri: l.avatar_url }} />

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#003f5c",
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,

        elevation: 5,
    }
});
