import * as React from 'react';
import { images, icons, SIZES, COLORS, FONTS } from '../constants';
import { Tab, Divider, SearchBar, Header, Icon, Card, ListItem, Avatar } from 'react-native-elements';
import {
    StyleSheet,
    View,
    Image,
    Text,
    TouchableOpacity,
    ScrollView
} from 'react-native';

var Case_ID;
var Case_No;
var Client_Name;
var Mobile_Number;
var Judge;
export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
        };
    }

    state = {
        search: '',
    };

    updateSearch = (search) => {
        this.setState({ search });
    };

    render() {
        const { items } = this.state;
        const { search } = this.state;
        const {  UserName} = this.state;
        fetch('http://192.168.10.9:3210/CasesData')
            .then(res => res.json())
            .then(result => {
                this.setState({
                    items: result
                });
            });
        // If server response message same as Data Matched
        var i = 0;
        while (items[i]) {
          if (items[i].Client_ID == global.MyID ) {
                Case_ID  = items[i].Case_ID;
                Case_No  = items[i].Case_No;
                Client_Name  = items[i].Client_Name;
                Mobile_Number  = items[i].Mobile_Number;
                Judge  = items[i].Judge;
                console.log("Successfully Login");
                break;
              }
          else {
            i++;
          }
        }
      
        return (
            <View style={styles.container}>
                <Divider style={{ width: "50%", margin: -5 }} />
                <Header
                    containerStyle={{ width: 400 }}
                    leftComponent={{ text: "", style: { color: 'black', fontSize: 20 } }}
                    placement="left"
                />
                <Card containerStyle={{ backgroundColor: "#003f5c" }} wrapperStyle={{}} >
                    <Card.Title style={{ color: COLORS.white, ...FONTS.h3 }}>Search Case</Card.Title>
                    <Card.Divider />
                    <View>
                        <SearchBar
                            placeholder="Type Here..."
                            onChangeText={this.updateSearch}
                            value={search}
                        />
                    </View>
                </Card>
                <Card containerStyle={{ backgroundColor: "#003f5c" }}  >
                    <Card.Title style={{ color: COLORS.white, ...FONTS.h3 }}>Cases</Card.Title>
                    <Card.Divider />
                    <View >
                    {
                                items.map((l, i) => (
                                    <TouchableOpacity
                                        style={{ justifyContent: 'center', marginHorizontal: SIZES.base }}
                                        onPress={() => { console.log("Law : " + l.name + " " + l.subtitle) }}
                                    >
                                        <ListItem key={i++} bottomDivider >
                                            <ListItem.Content >
                                                <ListItem.Title>Case No. {l.Case_No}</ListItem.Title>
                                                <ListItem.Subtitle>Client : {l.Client_Name}</ListItem.Subtitle>
                                                <ListItem.Subtitle>mobile : {l.Mobile_Number}</ListItem.Subtitle>
                                            </ListItem.Content>
                                        </ListItem>
                                    </TouchableOpacity>
                                ))
                            }
                    </View>
                </Card>

            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#003f5c",
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,

        elevation: 5,
    }
});
