import React, { useState, useEffect } from 'react';
import { View, Text, Button, StyleSheet, FlatList,SafeAreaView,TextInput,TouchableOpacity } from 'react-native';
import {
  Container,
  Card,
  UserInfo,
  UserImgWrapper,
  UserImg,
  UserInfoText,
  UserName,
  PostTime,
  MessageText,
  TextSection,
} from '../styles/MessageStyles';
const MessagesScreen = ({navigation}) => {
    const [filteredDataSource, setFilteredDataSource] = useState([]);

    useEffect(() => {
         fetch(global.link + '/Client_Chat/'+global.MyName)
            .then((res) => res.json())
            .then((responseJson) => {
                setFilteredDataSource(responseJson);
            })
            .then(result => {
                ({
                    item: result
                });
            })
            .catch((error) => {
                console.error(error);
            });
    }, []);
    return (
      <Container>
        <FlatList 
          data={filteredDataSource}
          keyExtractor={item=>item.lawyer}
          renderItem={({item}) => (
            <Card onPress={() => navigation.navigate('Cl_Chat', {userName: item.lawyer})}>
              <UserInfo>
                <UserImgWrapper>
                  <UserImg source={item.userImg} />
                </UserImgWrapper>
                <TextSection>
                  <UserInfoText>
                    <UserName>{item.lawyer}</UserName>
                    <PostTime>{item.messageTime}</PostTime>
                  </UserInfoText>
                  <MessageText>{item.message}</MessageText>
                </TextSection>
              </UserInfo>
            </Card>
          )}
        />
      </Container>
    );
};

export default MessagesScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    alignItems: 'center', 
    justifyContent: 'center'
  },
});
