import * as React from 'react';
import { Tab, Divider, Tile, Header, Icon, Card } from 'react-native-elements';
import { SafeAreaProvider } from "react-native-safe-area-context";


import {
    StyleSheet,
    View,
    Image,
    Text,
    TouchableOpacity
} from 'react-native';

import { LinearGradient } from 'expo-linear-gradient';

import { images, icons, COLORS, FONTS, SIZES } from '../constants';

const OptionItem = ({ bgColor, icon, label, onPress }) => {
    return (
        <TouchableOpacity
            style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}
            onPress={onPress}
        >
            <View style={[styles.shadow, { width: 60, height: 60 }]}>
                <LinearGradient
                    style={[{ flex: 1, alignItems: 'center', justifyContent: 'center', borderRadius: 15, backgroundColor: 'red' }]}
                    colors={bgColor}
                    start={{ x: 0, y: 0 }}
                    end={{ x: 0, y: 1 }}
                >
                    <Image
                        source={icon}
                        resizeMode="cover"
                        style={{
                            tintColor: "#003f5c",
                            width: 30,
                            height: 30,
                        }}
                    />
                </LinearGradient>
            </View>
            <Text style={{ marginTop: SIZES.base, color: COLORS.gray, ...FONTS.body3 }}>{label}</Text>
        </TouchableOpacity>
    )
}
function renderDestinations(item, index) {
    var destinationStyle = {};

    if (index == 0) {
        destinationStyle = { marginLeft: SIZES.padding, }
    }

    return (
        <TouchableOpacity
            style={{ justifyContent: 'center', marginHorizontal: SIZES.base, ...destinationStyle }}
            onPress={() => { navigation.navigate("Client") }}
        >
            <Image
                source={item.img}
                resizeMode="cover"
                style={{
                    width: SIZES.width * 0.28,
                    height: '82%',
                    borderRadius: 15
                }}
            />

            <Text style={{ marginTop: SIZES.base / 2, ...FONTS.h4 }}>{item.name}</Text>
        </TouchableOpacity>
    )
}
const Lawyer = ({ navigation }) => {

    // Dummy Data
    const [destinations, setDestinations] = React.useState([
        {
            id: 0,
            name: "Ihtesham",
            img: images.skiVilla,
        },
        {
            id: 1,
            name: "Arslan",
            img: images.climbingHills,
        },
        {
            id: 2,
            name: "Frozen Hills",
            img: images.frozenHills,
        },
        {
            id: 3,
            name: "Beach",
            img: images.beach,
        },
    ]);

    return (
        <View style={styles.container}>
            <Divider style={{ width: "95%", margin: 10 }} />
            <Header
                containerStyle={{ width: 400 }}
                leftComponent={{ text: "Lawyer Features!", style: { color: 'black', fontSize: 20 } }}
                placement="left"
            />
            {/* Options */}
            <View style={{ flex: 1, justifyContent: 'upper' }}>
                <View style={{ flexDirection: 'row', marginTop: SIZES.padding, paddingHorizontal: SIZES.base }}>
                    <OptionItem
                        icon={icons.airplane}
                        bgColor={['#46aeff', '#5884ff']}
                        label="Cases"
                        onPress={() => navigation.navigate("Lw_Cases")}
                    />
                    <OptionItem
                        icon={icons.train}
                        bgColor={['#fddf90', '#fcda13']}
                        label="Requests"
                        onPress={() => navigation.navigate("Lw_Requests")}
                    />
                    <OptionItem
                        icon={icons.bus}
                        bgColor={['#e973ad', '#da5df2']}
                        label="Profile"
                        onPress={() => navigation.navigate("Lw_Profile")}
                    />
                    <OptionItem
                        icon={icons.taxi}
                        bgColor={['#fcaba8', '#fe6bba']}
                        label="Reminder"
                        onPress={() => navigation.navigate("Lw_Reminder")}
                    />
                </View>

            </View>

            {/* Destination */}

            <View style={{ flex: 1.8 }}>
                <Header
                    containerStyle={{height: -50, width: 400 }}
                    leftComponent={{ text: "Overview", style: { color: 'black', fontSize: 20 } }}
                    placement="left"
                />
                <View style={{ flexDirection: 'colomn' }}>

                <Card containerStyle={{backgroundColor: "#003f5c"}} wrapperStyle={{}} >
                    <Card.Title style={{ color: COLORS.white, ...FONTS.h3 }}>Active Cases</Card.Title>
                    <Card.Divider />
                    <View
                        style={{
                            position: "relative",
                            alignItems: "center",
                        }}
                    >
                        <Text style={{ color: COLORS.white, ...FONTS.h3 }}> 5</Text>
                    </View>
                </Card>
                <Card containerStyle={{backgroundColor: "#003f5c"}} wrapperStyle={{}}>
                <Card.Title style={{ color: COLORS.white, ...FONTS.h3 }}>Active Clients</Card.Title>
                    <Card.Divider />
                    <View
                        style={{
                            position: "relative",
                            alignItems: "center"
                        }}
                    >
                        <Text style={{ color: COLORS.white, ...FONTS.h3 }}>4</Text>
                    </View>
                </Card>


                </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#003f5c",
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,

        elevation: 5,
    }
});

export default Lawyer;
