import React, { useEffect, useState } from 'react';
import { Divider, Header, Card } from 'react-native-elements';
import {
    StyleSheet,
    View,
    Image,
    Text,
    TouchableOpacity,
    Alert
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { icons, COLORS, FONTS, SIZES } from '../constants';
const App = ({ navigation }) => {
    const [count, setCount] = useState('');
    const [Requests, setRequests] = useState('');
    var numberPattern = /\d+/g;
    const CaseCount = () => {
        useEffect(() => {
            fetch(global.link + '/totalCases/' + global.MyName)
                .then((res) => res.json())
                .then((responseJson) => {
                    setCount(JSON.stringify(responseJson).match(numberPattern));
                })
                .catch((error) => {
                    console.error(error);
                });
        }, []);
    }
    const NewRequests = () => {
        useEffect(() => {
            fetch(global.link + '/Rejected_Request_Message/' + global.MyName)
                .then((res) => res.json())
                .then((responseJson) => {
                    setRequests(JSON.stringify(responseJson));
                })
                .catch((error) => {
                    console.error(error);
                });
        }, []);
    }
    const OptionItem = ({ bgColor, icon, label, onPress }) => {
        return (
            <TouchableOpacity
                style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}
                onPress={onPress}
            >
                <View style={[styles.shadow, { width: 60, height: 60 }]}>
                    <LinearGradient
                        style={[{ flex: 1, alignItems: 'center', justifyContent: 'center', borderRadius: 15, backgroundColor: 'red' }]}
                        colors={bgColor}
                        start={{ x: 0, y: 0 }}
                        end={{ x: 0, y: 1 }}
                    >
                        <Image
                            source={icon}
                            resizeMode="cover"
                            style={{
                                tintColor: "#003f5c",
                                width: 30,
                                height: 30,
                            }}
                        />
                    </LinearGradient>
                </View>
                <Text style={{ marginTop: SIZES.base, color: COLORS.gray, ...FONTS.body3 }}>{label}</Text>
            </TouchableOpacity>
        )
    }
    const AcceptingRequest = (Requests) => {
        Alert.alert(global.link + '/deleteRequest_Notification/' + global.MyName)

        fetch(global.link + '/deleteRequest_Notification/' + global.MyName, {
            method: 'DELETE'
        })
            .then(() =>
                navigation.navigate("Client"),
            )
            .catch(error => {
                setErrorMessage(error);
                console.error('There was an error!', error);
            });
    }
    return (
        CaseCount(),
        NewRequests(),
        console.log(Requests),
        <View style={styles.container}>
            <Divider style={{ width: "95%", margin: 10 }} />
            <Header
                containerStyle={{ width: 400 }}
                leftComponent={{ text: "Client Features!", style: { color: 'black', fontSize: 20 } }}
                placement="left"
            />
            {/* Options */}
            <View style={{ flex: 1, justifyContent: 'center' }}>
                <View style={{ flexDirection: 'row', marginTop: SIZES.padding, paddingHorizontal: SIZES.base }}>
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#46aeff', '#5884ff']}
                        label="Cases"
                        onPress={() => navigation.navigate('Cl_Cases')}
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#fddf90', '#fcda13']}
                        label="Lawyers"
                        onPress={() => navigation.navigate("Cl_Lawyer")}
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#e973ad', '#da5df2']}
                        label="Chatting"
                        onPress={() => navigation.navigate("message")}
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#fcaba8', '#fe6bba']}
                        label="Dictionary"
                        onPress={() => navigation.navigate("Cl_Dictionary")}
                    />
                </View>
                <View style={{ flexDirection: 'row', marginTop: SIZES.radius, paddingHorizontal: SIZES.base }}>
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#ffc465', '#ff9c5f']}
                        label="Hearings"
                        onPress={() => navigation.navigate("Cl_Hearings")}
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#7cf1fb', '#4ebefd']}
                        label="Profile"
                        onPress={() => navigation.navigate("Lw_Profile")}
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#7be993', '#46caaf']}
                        label="Reminder"
                        onPress={() => navigation.navigate("Cl_Reminder")}
                    />
                    <OptionItem
                        icon={icons.event}
                        bgColor={['#fca397', '#fc7b6c']}
                        label="Reviews"
                        onPress={() => navigation.navigate("Cl_Review")}
                    />
                </View>
            </View>
            <View style={{ flex: 1.8 }}>
                <Header
                    containerStyle={{ height: -50, width: 400 }}
                    leftComponent={{ text: "Notifications!", style: { color: 'black', fontSize: 20 } }}
                    placement="left"
                />
                <View style={{ flexDirection: 'colomn' }}>
                    <Card containerStyle={{ backgroundColor: "#003f5c" }} wrapperStyle={{}}>
                        <Card.Title style={{ color: COLORS.white, ...FONTS.h3 }}>Request Responce</Card.Title>
                        <Card.Divider />
                        <View
                            style={{
                                position: "relative",
                                alignItems: "center"
                            }}
                        >
                            <TouchableOpacity
                                onPress={() => AcceptingRequest(Requests)}
                            >

                                <Text style={styles.loginText}>{Requests}</Text>
                            </TouchableOpacity>
                            {/* <Text style={{ color: COLORS.white, ...FONTS.h3 }}>{Requests}</Text> */}
                        </View>
                    </Card>


                </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#003f5c",
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,

        elevation: 5,
    },
    loginBtn: {
        width: "80%",
        backgroundColor: "#fb5b5a",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 40,
        marginBottom: 10
    },
    loginText: {
        color: "white"
    }
});

export default App;
